(51,	'avalara_q3-2023_noAvaTax',	'marketing',	'en_US',	'Automatically calculate VAT in real time',	'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.',	'{}',	'pending',	'woocommerce.com',	'2024-11-25 17:20:31',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(52,	'woocommerce-usermeta-Sept2023-productvendors',	'update',	'en_US',	'Your store requires a security update',	'<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.',	'{}',	'pending',	'woocommerce.com',	'2024-11-25 17:20:31',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(53,	'woocommerce-STRIPE-Oct-2023-update-needed',	'update',	'en_US',	'Action required: Security update for WooCommerce Stripe Gateway',	'<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.',	'{}',	'pending',	'woocommerce.com',	'2024-11-25 17:20:31',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(54,	'amazon-mcf-reviews-2023-12-07',	'marketing',	'en_US',	'Enjoying Amazon MCF for WooCommerce?',	'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!',	'{}',	'pending',	'woocommerce.com',	'2024-11-25 17:20:31',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(55,	'remove_estimated_deposits_2024',	'marketing',	'en_US',	'Estimated deposits are going away',	'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.',	'{}',	'pending',	'woocommerce.com',	'2024-11-25 17:20:31',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(56,	'wc-payments-notes-set-https-for-checkout',	'info',	'en_US',	'Enable secure checkout',	'Enable HTTPS on your checkout pages to display all available payment methods and protect your customers data.',	'{}',	'unactioned',	'woocommerce-payments',	'2024-11-25 17:24:08',	NULL,	0,	'plain',	'',	1,	1,	'info'),
(57,	'wc-admin-add-first-product-note',	'email',	'en_US',	'Add your first product',	'{greetings}<br /><br />Nice one; you\'ve created a WooCommerce store! Now it\'s time to add your first product and get ready to start selling.<br /><br />There are three ways to add your products: you can <strong>create products manually, import them at once via CSV file</strong>, or <strong>migrate them from another service</strong>.<br /><br /><a href=\"https://woocommerce.com/document/managing-products/?utm_source=help_panel&amp;utm_medium=product\">Explore our docs</a> for more information, or just get started!',	'{\"role\":\"administrator\"}',	'unactioned',	'woocommerce-admin',	'2024-11-30 08:41:57',	NULL,	0,	'plain',	'http://localhost:10010/wp-content/plugins/woocommerce/images/admin_notes/dashboard-widget-setup.png',	0,	0,	'info'),
(58,	'wc-admin-choosing-a-theme',	'marketing',	'en_US',	'Choosing a theme?',	'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.',	'{}',	'unactioned',	'woocommerce-admin',	'2024-11-30 08:41:57',	NULL,	0,	'plain',	'',	1,	1,	'info'),
(59,	'wc-admin-marketing-jetpack-backup',	'marketing',	'en_US',	'Protect your WooCommerce Store with Jetpack Backup.',	'Store downtime means lost sales. One-click restores get you back online quickly if something goes wrong.',	'{}',	'unactioned',	'woocommerce-admin-notes',	'2024-11-30 08:41:57',	NULL,	0,	'thumbnail',	'http://localhost:10010/wp-content/plugins/woocommerce/assets/images/admin_notes/marketing-jetpack-2x.png',	1,	1,	'info'),
(60,	'wc-admin-mobile-app',	'info',	'en_US',	'Install Woo mobile app',	'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.',	'{}',	'unactioned',	'woocommerce-admin',	'2024-11-30 08:41:57',	NULL,	0,	'plain',	'',	1,	1,	'info'),
(61,	'paypal_promo_Q4_2024',	'marketing',	'en_US',	'Add PayPal to your existing checkout this holiday season',	'Drive conversion and give customers more confidence to buy when you complement your checkout with PayPal, Venmo, Pay Later, and more. Attract new shoppers and give them a streamlined buying experience with Fastlane, PayPal’s fast and simple guest checkout.',	'{}',	'pending',	'woocommerce.com',	'2024-11-30 08:42:02',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(62,	'wc-admin-launch-checklist',	'info',	'en_US',	'Ready to launch your store?',	'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.',	'{}',	'unactioned',	'woocommerce-admin',	'2024-12-01 07:50:51',	NULL,	0,	'plain',	'',	0,	0,	'info'),
